mapper.select:
select screen_id, screen_content, uid, like_times, dislike_times, associate_topics, associate_users, post_time, edit_time, edit_times, is_public, system_review_result from user_screen_2022
uid = #{uid}
and system_review_result=1
order by post_time desc
limit #{limit} offset #{offset}

select screen_id, screen_content, uid, like_times, dislike_times, associate_topics, associate_users, post_time, edit_time, edit_times, is_public, system_review_result FROM user_screen_2022 AS t1 JOIN (SELECT ROUND(RAND() * (SELECT MAX(id) FROM user_screen_2022)) AS id) AS t2 WHERE t1.id >= t2.id and t1.system_review_result=1 and t1.is_public=1
order by post_time desc
limit #{limit} offset #{offset}

mapper.update:
update user_screen_2022
set system_review_result = #{systemReviewResult}
where screen_id = #{screenId};

update user_screen_2022
set is_public = #{isPublic}
where screen_id = #{screenId};
